  Speedtest.prototype.start = function() {
    if (this._isTesting) {
      return;
    }

    this._isTesting = true;

    // Detect network connection type and speed

    this._detectNetwork();

    // Choose a server

    this._chooseServer();

    // Start download test

    this._startDownload();
  }

  Speedtest.prototype._detectNetwork = function() {
    var networkInfo = navigator.connection;

    if (networkInfo && networkInfo.type) {
      this._networkType = networkInfo.type;
      this._bandwidth = networkInfo.downlink;
    } else {
      this._networkType = "Unknown";
      this._bandwidth = 0;
    }
  }

  Speedtest.prototype._chooseServer = function() {
    var servers = speedtest.servers;
    var server = null;

    for (var i = 0; i < servers.length; i++) {
      if (servers[i].distance <= this._bandwidth) {
        server = servers[i];
        break;
      }
    }

    if (!server) {
      server = servers[0];
    }

    this._server = server;
  }

  Speedtest.prototype._startDownload = function() {
    var xhr = new XMLHttpRequest();
    xhr.open("GET", this._server.url, true);
    xhr.responseType = "arraybuffer";
    xhr.onload = this._onDownloadComplete;
    xhr.send();
  }

  Speedtest.prototype._onDownloadComplete = function(event) {
    var xhr = event.target;

    if (xhr.status === 200) {
      this._download = xhr.response.byteLength / 1048576;
    } else {
      this._download = null;
    }

    this._startUpload();
  }

